targets.expr:
(
  label_replace(
    sum by (namespace, container) (
      kube_pod_container_resource_limits{
        namespace="$namespace",
        resource="cpu",
        container!="istio-proxy"
      }
    ),
    "deployment",
    "$1",
    "container",
    "(.*)"
  )
)

(
  label_replace(
    sum by (namespace, container) (
      kube_pod_container_resource_limits{
        namespace="$namespace",
        resource="memory",
        container!="istio-proxy"
      }
    ),
    "deployment",
    "$1",
    "container",
    "(.*)"
  )
)/ 1024 / 1024 /1024
